// Location: ./commands/compareScreenshot.js
exports.command = function(selector, filename, expected, callback) {
    var self = this,
        resultPath = 'test/screenshots/results/' + filename;

    self.saveElementScreenshot(selector, filename, function(callback) {
        self.compareScreenshot(filename, expected, function (result) {
            if (typeof callback === 'function') {
                callback.call(self, result);

            }
        })
    });
    return this; // allows the command to be chained.
};